mapper.select:
select 

from BIZ_TD_PLATFORM_DETAIL
where APP_NO = #{appNo,jdbcType=VARCHAR}
  and RULE_ID = #{ruleId,jdbcType=VARCHAR}
  and TYPE = #{type,jdbcType=VARCHAR}

mapper.delete:
delete from BIZ_TD_PLATFORM_DETAIL
where APP_NO = #{appNo,jdbcType=VARCHAR}
  and RULE_ID = #{ruleId,jdbcType=VARCHAR}
  and TYPE = #{type,jdbcType=VARCHAR}

mapper.update:
update BIZ_TD_PLATFORM_DETAIL
set DESCRIPTION = #{description,jdbcType=VARCHAR},
  PLATFORM_COUNT = #{platformCount,jdbcType=VARCHAR},
  PLATFORM_DETAIL_DIMENSION = #{platformDetailDimension,jdbcType=VARCHAR},
  PLATFORM_DETAIL = #{platformDetail,jdbcType=VARCHAR},
  INPUT_DATE = #{inputDate,jdbcType=VARCHAR},
  INPUT_TIME = #{inputTime,jdbcType=VARCHAR},
  LAST_UPDATE_DATE = #{lastUpdateDate,jdbcType=VARCHAR},
  LAST_UPDATE_TIME = #{lastUpdateTime,jdbcType=VARCHAR}
where APP_NO = #{appNo,jdbcType=VARCHAR}
  and RULE_ID = #{ruleId,jdbcType=VARCHAR}
  and TYPE = #{type,jdbcType=VARCHAR}
