mapper.select:
select 

from BIZ_TD_RISK_RULES_INFO
where APP_NO = #{appNo,jdbcType=VARCHAR}
  and RULE_ID = #{ruleId,jdbcType=VARCHAR}
  and TYPE = #{type,jdbcType=VARCHAR}

mapper.delete:
delete from BIZ_TD_RISK_RULES_INFO
where APP_NO = #{appNo,jdbcType=VARCHAR}
  and RULE_ID = #{ruleId,jdbcType=VARCHAR}
  and TYPE = #{type,jdbcType=VARCHAR}

mapper.update:
update BIZ_TD_RISK_RULES_INFO

  
    SUSPECT_TEAM_DETAIL_LIST = #{suspectTeamDetailList,jdbcType=VARCHAR},
  
  
    INPUT_DATE = #{inputDate,jdbcType=VARCHAR},
  
  
    INPUT_TIME = #{inputTime,jdbcType=VARCHAR},
  
  
    LAST_UPDATE_DATE = #{lastUpdateDate,jdbcType=VARCHAR},
  
  
    LAST_UPDATE_TIME = #{lastUpdateTime,jdbcType=VARCHAR},
  

where APP_NO = #{appNo,jdbcType=VARCHAR}
  and RULE_ID = #{ruleId,jdbcType=VARCHAR}
  and TYPE = #{type,jdbcType=VARCHAR}

update BIZ_TD_RISK_RULES_INFO
set SUSPECT_TEAM_DETAIL_LIST = #{suspectTeamDetailList,jdbcType=VARCHAR},
  INPUT_DATE = #{inputDate,jdbcType=VARCHAR},
  INPUT_TIME = #{inputTime,jdbcType=VARCHAR},
  LAST_UPDATE_DATE = #{lastUpdateDate,jdbcType=VARCHAR},
  LAST_UPDATE_TIME = #{lastUpdateTime,jdbcType=VARCHAR}
where APP_NO = #{appNo,jdbcType=VARCHAR}
  and RULE_ID = #{ruleId,jdbcType=VARCHAR}
  and TYPE = #{type,jdbcType=VARCHAR}
